$bg: #2d3a4b;
$dark_gray: #889aa4;
$light_gray: #eee;

.login-container {
    background-image: url("../../assets/robin.jpeg");
    background-repeat: no-repeat;
    background-size: 100% 100%;
    min-height: 100%;
    width: 100%;
    background-color: gainsboro;
    overflow: hidden;
    .login-form {
        border-radius: 20px;
        background-color: rgb(41, 40, 40);
        opacity: 0.9;
        position: relative;
        width: 520px;
        max-width: 100%;
        padding: 20px 35px;
        margin: 12% auto;
        overflow: hidden;
    }

    .tips {
        font-size: 12px;
        color: #fff;
        margin-top: 15px;

        span {
            padding-left: 10px;
            &:first-of-type {
                margin-right: 16px;
            }
        }
        // img{
        //     margin-right: 16px;
        // }
    }
    .passport {
        font-size: 12px;
        color: #fff;
        height: 30px;
    }
    .svg-container {
        padding: 6px 5px 6px 15px;
        color: $dark_gray;
        vertical-align: middle;
        width: 30px;
        display: inline-block;
    }

    .title-container {
        position: relative;

        .title {
            font-size: 26px;
            color: $light_gray;
            margin: 0px auto 40px auto;
            text-align: center;
            font-weight: bold;
        }
    }

    .show-pwd {
        position: absolute;
        right: 10px;
        top: 7px;
        font-size: 16px;
        color: $dark_gray;
        cursor: pointer;
        user-select: none;
    }

    .thirdparty-button {
        position: absolute;
        right: 0;
        bottom: 6px;
    }

    @media only screen and (max-width: 470px) {
        .thirdparty-button {
            display: none;
        }
    }
}
